    private int pageSize = 25;
    const int basePage = 0;

    //Method to proceed to next page 
    private void Next()
    {
        if (pageStart <= pageEnd)
        {
            pageStart += pageSize;
            pageEnd += pageSize;
        }//if statement to prevent previous page button working when there is no results to show - not working 
